using System;
using System.Drawing;
using CoreGraphics;
using Foundation;
using UIKit;

namespace BTProgressHUD
{
    public static class ImageHelper
    {
        public static UIImage ErrorOutlineImage => ""
                .ToUIImage();

        public static UIImage ErrorOutlineFullImage => ""
                .ToUIImage();
        
        public static UIImage SuccessOutlineImage => ""
                .ToUIImage ();
        
        public static UIImage SuccessOutlineFullImage => ""
            .ToUIImage ();
        
        public static UIImage InfoOutlineImage => ""
                .ToUIImage ();
        
        public static UIImage InfoOutlineFullImage => ""
            .ToUIImage ();
        
        static UIImage ToUIImage (this string base64String)
        {
            var imageBytes = Convert.FromBase64String(base64String);
            var imageData = NSData.FromArray(imageBytes);
            var uiImage = UIImage.LoadFromData(imageData);

            return uiImage;
        }
    }
}
